def arrlength(arr)
puts "il y a #{arr.length} handle dans cette array"
end

def shorterhandle(arr)
puts "Le handle le plus court dans cette liste est #{arr.min {|a, b| a.length <=> b.length}}"
end

def fivecharlength(arr)
arr.select {|str| str.delete!('@')}
puts "Il y a #{arr.count {|str| str.length == 5}} handle avec 5 caractères"
end

def upperfirstchar(arr)
puts "Il y a #{arr.count { |str| str.match?(/\@[A-Z]/)}} handle qui commencent par une majuscule"
puts "Il y a #{arr.count { |str| str.match?(/\@[a-z]/)}} handle qui commencent par une minuscule"
puts "Il y a #{arr.count { |str| str.match?(/\@[_]/)}} handle qui commencent par un underscore"
end

def sortatoz(arr)
puts "La liste suivante est classée par ordre alphabétique"
puts "#{arr.sort_by(&:downcase)}"
end

def handleperlength(arr)
puts arr.sort_by(&:length)
end

def handlefinder(arr)
puts "La position du handle @epenser est : #{arr.index("@epenser")}"
end

def bygroup(arr)
# puts arr.group_by(&:length).transform_values(&:count)
puts arr.map(&:size).tally
end

