    user_input = gets.chomp.to_i

    if user_input == 1
        arrlength(arr)
    elsif user_input == 2
        shorterhandle(arr)
    elsif user_input == 3
        fivecharlength(arr)
    elsif user_input == 4
        upperfirstchar(arr)
    elsif user_input == 5
        sortatoz(arr)
    elsif user_input == 6
        handleperlength(arr)
    elsif user_input == 7
        handlefinder(arr)
    elsif user_input == 8
        bygroup(arr)
    elsif user_input == 9
        puts "Fermeture de l'application"
    else "Vous devez taper un numéro entre 1 et 9"
    end
